	(33, 1, 'Home Page - Trading Software', 'Home Page - Trading Software', 'Home-Page-Trading-Software', 'Home Page - Trading Software', 'Home Page - Trading Software', '&lt;div class=&quot;ts_hdr tac f18 economica_bold row_hdr&quot;&gt;TRADING SOFTWARES&lt;/div&gt;\r\n&lt;img src=&quot;misc/css/images/banner2.png&quot; alt=&quot;trading software&quot;&gt;\r\n&lt;ul&gt;\r\n	&lt;li&gt;&lt;a href=&quot;pages/Download-MetaTrader-4&quot; class=&quot;fl&quot;&gt;Download MetaTrader 4&lt;/a&gt;&lt;a href=&quot;pages/About-Metatrader-4&quot; class=&quot;fr&quot;&gt;About MetaTrader 4&lt;/a&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;a href=&quot;pages/MetaTrader-4-User-Guide&quot; class=&quot;fl&quot;&gt;MetaTrader 4 User Guide&lt;/a&gt;&lt;a href=&quot;pages/Automated-Trading&quot; class=&quot;fr&quot;&gt;Automated Trading&lt;/a&gt;&lt;/li&gt;\r\n	&lt;li&gt;&lt;a href=&quot;pages/Multiterminal-MAM&quot; class=&quot;fl&quot;&gt;Multiterminal MAM&lt;/a&gt;&lt;/li&gt;\r\n&lt;/ul&gt;', '1', '2013-02-25 17:54:56', 1, 1, '2013-02-26 23:24:57', '127.0.0.1'),
